/**
 * @about ╦Ы╬щurlеп╤ойгдз╡©лЬв╙╩╧йглЬЁЖрЁцФё╛╡╒╫ЬпплЬв╙
 * @param
 *      url:{а╢╫с(http://www.baidu.com),дз╡©лЬв╙({name:'test'})},
 *      $router:б╥си
 * @return нч╥╣╩ьж╣ё╛╫ЬппрЁцФлЬв╙
 * @author bob
 */
exports.go = function (url, $router, replace) {
    if (/^javas/.test(url) || !url) {
        return;
    }
    const useRouter = typeof url === 'object' || ($router && typeof url === 'string' && !/http/.test(url));
    if (useRouter) {
        if (replace || url.urlreplace) {
            $router.replace(url);
        } else {
            $router.push(url);
        }

    } else {
        window.open(url, '_blank');
    }
}

/**
 * @about ╦Ы╬щurlеп╤ойгдз╡©лЬв╙╩╧йглЬЁЖрЁцФё╛╡╒╫ЬпплЬв╙
 * @param
 *      url:{а╢╫с(http://www.baidu.com),дз╡©лЬв╙({name:'test'})},
 *      $router:б╥си
 * @return нч╥╣╩ьж╣ё╛╫ЬппрЁцФлЬв╙
 * @author bob
 */
exports.back = function ($router, step) {
    let stp = step ? -step : -1;
    $router.go(stp);
}


