/**
 * @about ╦Яй╫╩╞й╠╪Д
 * @param
 *      date:{number╩Рdate}╨ацКй╠╪Дё╛╠ьлН
 *      fmt:{string}пХр╙иЗЁи╣дй╠╪Д╦Яй╫ё╛я║лН д╛хо  MM-dd hh:mm
 * @return string   ╦Яй╫╩╞й╠╪Двж╥Ш╢╝
 * @author bob
 */
exports.formatTime = function (date, format) {
    if (typeof date == "number") {
        date = new Date(date);
        var o = {
            "M+": date.getMonth() + 1, //тб╥щ
            "d+": date.getDate(), //ху
            "h+": date.getHours(), //п║й╠
            "m+": date.getMinutes(), //╥ж
            "s+": date.getSeconds(), //цК
            "q+": Math.floor((date.getMonth() + 3) / 3), //╪╬╤х
            "S": date.getMilliseconds() //╨ацК
        };
        var fmt = format || "yyyy-MM-dd hh:mm";
        if (/(y+)/.test(fmt))
            fmt = fmt.replace(RegExp.$1, (date.getFullYear() + "").substr(4 - RegExp.$1.length));
        for (var k in o)
            if (new RegExp("(" + k + ")").test(fmt))
                fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
        return fmt;
    } else {
        return date;
    }

}

/**
 * @about ╦Яй╫╩╞╪ш╦Я
 * @param
 *      s:{string}  р╙в╙╩╞╣д╪ш╦Я╣дйЩд©ё╛╣╔н╩ ║╟╥ж║╠
 * @return string   ╦Яй╫╩╞╣д╪ш╦Явж╥Ш╢╝
 * @author bob
 */
exports.formatPrice = function (s) {
    var n = 2;
    s = parseInt(s) / 100 + "";
    s = parseFloat((s + "").replace(/[^\d\.-]/g, "")).toFixed(n) + "";
    var l = s.split(".")[0].split("").reverse(), r = s.split(".")[1];
    var t = "";
    for (var i = 0; i < l.length; i++) {
        t += l[i] + ((i + 1) % 3 == 0 && (i + 1) != l.length ? "," : "");
    }
    return t.split("").reverse().join("") + "." + r;
}


